import React,{useState, useEffect} from 'react';
import app from '../firebase/base.js'
import CIS5800_anno_edit from './cis5800_anno_edit';


const SORT_OPTIONS = {
    'TITLE_ASC': {column: 'anno', direction:'asc'},
    'TITLE_DESC': {column: 'anno', direction:'desc'},
}

function useTimes(sortBy= 'TITLE_ASC') {
    const [times,setTimes] =useState([])

    useEffect(()=>{
        const unsubscribe = app
        .firestore()
        .collection('CIS5800_announcement')
        .orderBy(SORT_OPTIONS[sortBy].column, SORT_OPTIONS[sortBy].direction)
        .onSnapshot((snapshot) =>{
            const newAnnos = snapshot.docs.map((doc) => ({
                id: doc.id,
                ...doc.data()
            }))
            setTimes(newAnnos)
        })
        return() =>unsubscribe()
    }, [sortBy])

    return times
}

const AnnoList = () =>{
    const [sortBy ,setSortBy] = useState('TITLE_ASC')
    const times = useTimes(sortBy)


    return(
        <div>
            <ol>
                {times.map((time)=>
                <li className="expand" key={time.id} >
                <div className="time-entry">
                    <h4><strong>{time.title}</strong></h4>
                    <div className="time"><strong>By: {time.time}</strong></div>
                </div>
                <div className="blogbody layoutbg">
                <p>{time.note}</p>
                </div>
                <hr/>
            </li>
                )}
            </ol>

        </div>
    )
}
export default AnnoList;
