import React from "react";
import firebase from '../firebase/base'
import {Button,ButtonGroup} from 'react-bootstrap';

export const cis4160_add_anno = ({ CIS4160_Announcement }) => {
  const [anno, setAnno] = React.useState(CIS4160_Announcement.anno);

  const onUpdate = () => {
    const db = firebase.firestore()
    db.collection('CIS4160_Announcement').doc(CIS4160_Announcement.id).set({...CIS4160_Announcement, anno})
  }

  const onDelete = () => {
    const db = firebase.firestore()
    db.collection('CIS4160_Announcement').doc(CIS4160_Announcement.id).delete()
  }

  return (
    <>
      <textarea className="expand todolist"
        value={anno}
        onChange={e => {
          setName(e.target.value);
        }}
      />
      <ButtonGroup vertical className="buttonheight">
      <Button size='sm' variant="primary" className="expand" onClick={onUpdate}><strong>Update</strong></Button>
      <Button size='sm' variant="primary" className="expand" onClick={onDelete}><strong>Delete</strong></Button>
      </ButtonGroup>
      <hr/>
    </>
  );
};
