import React,{useState, useEffect} from 'react';
import app from './base.js'
import Class1 from './popwindow';

const SORT_OPTIONS = {
    'TITLE_ASC': {column: 'title', direction:'asc'},
    'TITLE_DESC': {column: 'title', direction:'desc'},
}

function useTimes(sortBy= 'TITLE_ASC') {
    const [times,setTimes] =useState([])

    useEffect(()=>{
        const unsubscribe = app
        .firestore()
        .collection('tasks')
        .orderBy(SORT_OPTIONS[sortBy].column, SORT_OPTIONS[sortBy].direction)
        .onSnapshot((snapshot) =>{
            const newTimes = snapshot.docs.map((doc) => ({
                id: doc.id,
                ...doc.data()
            }))
            setTimes(newTimes)
        })
        return() =>unsubscribe()
    }, [sortBy])

    return times
}

const TimesList = () =>{
    const [sortBy ,setSortBy] = useState('TITLE_ASC')
    const times = useTimes(sortBy)


    return(
        <div>
            <br/>
            <h2>To Do List:</h2> 
            <hr/>
            <div >
                <label><strong>Sort: </strong></label>{' '}
                 <select value={sortBy} onChange={e => setSortBy(e.currentTarget.value)} >
                     <option value="TITLE_ASC">A-Z</option>
                     <option value="TITLE_DESC">Z-A</option>
                 </select>
            </div>
            <ol>

                {times.map((time)=>
                <li key={time.id} className="layoutbg">
                <div className="time-entry">
                    <strong>{time.title}</strong>
                    <div className="time"><strong>Lv: {time.time_seconds}</strong></div>
                </div>
                <div>
                <code><strong>Note:  </strong>{time.note}</code>
                </div>

            </li>
                )}

            </ol>
            <Class1/>
            <br/>
        </div>
    )
}
export default TimesList;
