import React, {Component} from 'react';
import {Row,Col,Button,Container,ListGroup,Tab,Tabs,ButtonGroup,Table} from 'react-bootstrap';
import NavBar from '../Professor/NavBar';
import '../Professor/professor.css';
import '../style_sheet/effect.css';
import Announcement from './Announcement';
import Cis4160_agenda from './cis4160_agenda';
import Assignment from './Assignment';
import{Link} from 'react-router-dom';
import CreateButton from './popwindow';
import firebase from '../firebase/base';

class GradingPage extends React.Component {
  state={
    allStudent: [],
    student: [],
    newStudent:[],
    assignment: [],
    project: [],
    exam: []
  }


  componentDidMount(){
      var db = firebase.firestore()
      db.collection('user').get().then(snapshot=>{
        snapshot.forEach( user => {
          console.log(user.data())
        });

      })
      db.doc(this.props.location.state.userCourse).collection('assignment').get().then( snapshot =>{
          snapshot.forEach(doc=>{
            this.setState({assignment:[...this.state.assignment, doc.data()]})
            db.doc(this.props.location.state.userCourse).collection('assignment').doc(doc.id).collection('student').get().then(query=>{
              query.forEach(querysnapshot=>{
                  this.setState({student:[...this.state.student, querysnapshot.data()]})
              })
            })
          })
      })
  }
  render(){
    console.log(this.state.student);
    console.log(this.state.assignment);
return (
<div className="P_HomeBG">
    <NavBar/>
  <Container>
  <Row className="rowcenter toprow">
  <Col md={12}>
  <h3>Dummy Grading </h3>
  </Col>
  </Row>
  <hr/>
  <Row>
  <Tab.Container defaultActiveKey="#Assignments">
  <Row>
    <Col xs={3} md={2}>
      <ListGroup>
        <ListGroup.Item className="expand list layoutbg classmenu" action href="#Assignments">
          <p>Assignments</p>
        </ListGroup.Item>
        <ListGroup.Item className="expand list layoutbg classmenu" action href="#Projects">
          <p>Projects</p>
        </ListGroup.Item>
        <ListGroup.Item className="expand list layoutbg classmenu" action href="#Exams">
          <p>Exams</p>
        </ListGroup.Item>
      </ListGroup>
    </Col>
    <Col xs={9} md={10}>
      <Tab.Content>
        <Tab.Pane eventKey="#Assignments">
          <Table striped bordered hover>
          <Row className='maxi'>

          <Col sm={2} md={2}>Name</Col>
          {
            this.state.assignment.map(assignment=>{
              if(assignment.category == 'Assignment'){
                this.state.student.map(student=>{
                  if(student.assignmentId = assignment.id){
                    console.log(student.grade);
                    return(<Col sm={1} md={1}>{student.grade}</Col>)
                  }
                })
              }
            })
          }

            </Row>
            <Row>
              <Col sm={2} md={2}></Col>
              <Col sm={1} md={1}></Col>
              <Col sm={1} md={1}></Col>
              <Col sm={1} md={1}></Col>
              <Col sm={1} md={1}></Col>
              <Col sm={1} md={1}></Col>
              <Col sm={1} md={1}></Col>
              <Col sm={1} md={1}></Col>
              <Col sm={1} md={1}></Col>
              <Col sm={1} md={1}></Col>
              <Col sm={1} md={1}></Col>
            </Row>
          </Table>
        </Tab.Pane>
        <Tab.Pane eventKey="#Projects">
        <Table striped bordered hover>
            <Row>
              <Col xs={2}sm={2} md={2}><p>Name</p></Col>
              {
                this.state.assignment.map(assignment=>{
                  if(assignment.category == 'Project'){
                    return(<Col sm={1} md={1}>{assignment.title}</Col>)
                  }
                })
              }
            </Row>
            <Row>
              <Col sm={2} md={2}></Col>
              <Col sm={1} md={1}></Col>
              <Col sm={1} md={1}></Col>
              <Col sm={1} md={1}></Col>
              <Col sm={1} md={1}></Col>
              <Col sm={1} md={1}></Col>
              <Col sm={1} md={1}></Col>
              <Col sm={1} md={1}></Col>
              <Col sm={1} md={1}></Col>
              <Col sm={1} md={1}></Col>
              <Col sm={1} md={1}></Col>
            </Row>
          </Table>

        </Tab.Pane>
        <Tab.Pane eventKey="#Exams">
        <Table striped bordered hover>
            <Row>
              <Col sm={2} md={2}>Name</Col>
              {
                this.state.assignment.map(assignment=>{
                  if(assignment.category == 'Exam'){
                    return(<Col sm={1} md={1}>{assignment.title}</Col>)
                  }
                })
              }
            </Row>
            <Row>
              <Col sm={2} md={2}></Col>
              <Col sm={1} md={1}></Col>
              <Col sm={1} md={1}></Col>
              <Col sm={1} md={1}></Col>
              <Col sm={1} md={1}></Col>
              <Col sm={1} md={1}></Col>
              <Col sm={1} md={1}></Col>
              <Col sm={1} md={1}></Col>
              <Col sm={1} md={1}></Col>
              <Col sm={1} md={1}></Col>
              <Col sm={1} md={1}></Col>
            </Row>
          </Table>

        </Tab.Pane>
      </Tab.Content>
    </Col>
  </Row>
</Tab.Container>
  </Row>
      </Container>



</div>
);
}
}

export default GradingPage;
