import React from "react";
import {Button, ButtonToolbar, Modal,Form,Col} from 'react-bootstrap';
import baruch from '../images/baruchlogo.png';
import firebase from '../firebase/base'

export class MyVerticallyCenteredModal extends React.Component {
  state ={
    category:null,
    title: null,
    gradeWeight: null,
    description: null
  }
  componentDidMount(){
    const db = firebase.firestore()
    db.doc(props.state.userCourse).collection('assignment').add({category: category,
                                                                                    title: title,
                                                                                    gradeWeight: gradeWeight,
                                                                                    description: description})
    }
      render(){
    return (
      <Modal
        {...props}
        size="lg"
        aria-labelledby="contained-modal-title-vcenter"
        centered
      >
        <Modal.Header>
          <Modal.Title id="contained-modal-title-vcenter">
          <center><img src={baruch} width="30%" align-item="right" alt="baruchlogo"/></center>
          </Modal.Title>
        </Modal.Header>
        <Modal.Body>
        <Form>
        <Form.Group>
            <Form.Label><b>Category</b></Form.Label>
            <Form.Control as="select" value="Choose..." onChange={e => {this.setState({category:e.target.value})}} >
                <option>Category...</option>
                <option>Assignment</option>
                <option>Project</option>
                <option>Exam</option>
            </Form.Control>
        </Form.Group>
        <Form.Row>
            <Form.Group as={Col} >
            <Form.Label><b>Title</b></Form.Label>
            <Form.Control type="text" placeholder="Enter Title" onChange={e => {this.setState({title:e.target.value})}} />
            </Form.Group>

            <Form.Group as={Col}>
            <Form.Label><b>Grade Weight</b></Form.Label>
            <Form.Control type="text" placeholder="Enter Grade Weight" onChange={e => {this.setState({gradeWeight:e.target.value})}}/>
            </Form.Group>
        </Form.Row>

        <Form.Group>
            <Form.Label><b>Description</b></Form.Label>
            <Form.Control as="textarea" rows="5" placeholder="Enter Description" onChange={e => {this.setState({description:e.target.value})}}/>
        </Form.Group>

        <Button variant="primary" onClick={onCreate}>
            <b>Post</b>
        </Button>

        </Form>

        </Modal.Body>
        <Modal.Footer>
          <Button variant="secondary" onClick={props.onHide}>Close</Button>
        </Modal.Footer>
      </Modal>
    );
  }
  }

  function PopWindow(props) {
    console.log(props.state)
    const [modalShow, setModalShow] = React.useState(false);

    return (
      <ButtonToolbar>
        <Button size='sm' className="blogicon expand" variant="primary" onClick={() => setModalShow(true)}>
          <strong>Create</strong>
        </Button>

        <MyVerticallyCenteredModal
          state={props.state}
          show={modalShow}
          onHide={() => setModalShow(false)}
        />
      </ButtonToolbar>
    );
  }
  export default PopWindow;
