import React,{ useState } from 'react'
import app from '../firebase/base.js'

const AddAnnoEntryForm = () => {
    const [anno, setAnno] =useState('')
    const [date, setDate] =useState('')
    const [content,setContent] = useState('')

    function onSubmit(e) {
        e.preventDefault()

        app
        .firestore()
        .collection('CIS5800_announcement')
        .add({
            anno,
            date,
            content
        })
        .then(() =>{
            setAnno('')
            setDate('')
            setContent('')
        })
    }

    return(
        <form onSubmit={onSubmit} className="logform">
            <div>
                <label><strong>Announcement Title:</strong></label>
                <input placeholder="Title here..." className="expand blogtextbox layoutbg" type="text" value={anno} onChange={e=>setAnno(e.currentTarget.value)}/>
            </div>
            <br/>
            <div>
                <label><strong>Date:</strong></label>
                <input placeholder="MM/DD/YYYY" className="expand blogtextbox layoutbg" type="text" value={date} onChange={e=>setDate(e.currentTarget.value)}/>   
            </div>
            <br/>
            <div>
                <label for ><strong>Content:</strong></label>
                <br/>
                <textarea  placeholder="Content..." className="expand bloginput layoutbg" type="text" value={content} onChange={e=>setContent(e.currentTarget.value)}/>   
            </div>
            <br/>
            <button variant="primary"><strong>Post</strong></button>

        </form>
    )
}

export default AddAnnoEntryForm;
