import React,{useState, useEffect} from 'react';
import app from './base.js'
import Class1 from './popwindow';

const SORT_OPTIONS = {
    'TITLE_ASC': {column: 'title', direction:'asc'},
    'TITLE_DESC': {column: 'title', direction:'desc'},
}

function useTimes(sortBy= 'TITLE_ASC') {
    const [titles,setTitles] =useState([])

    useEffect(()=>{
        const unsubscribe = app
        .firestore()
        .collection('blogs')
        .orderBy(SORT_OPTIONS[sortBy].column, SORT_OPTIONS[sortBy].direction)
        .onSnapshot((snapshot) =>{
            const newTitles = snapshot.docs.map((doc) => ({
                id: doc.id,
                ...doc.data()
            }))
            setTitles(newTitles)
        })
        return() =>unsubscribe()
    }, [sortBy])

    return titles
}

const TitleList = () =>{
    const [sortBy ,setSortBy] = useState('TITLE_ASC')
    const titles = useTitles(sortBy)

    return(
        <div>
            <br/>
            <h2>To Do List:</h2> 
            <hr/>
            <div >
                <label><strong>Sort: </strong></label>{' '}
                 <select value={sortBy} onChange={e => setSortBy(e.currentTarget.value)} >
                     <option value="TITLE_ASC">A-Z</option>
                     <option value="TITLE_DESC">Z-A</option>
                 </select>
            </div>
            <ol>
                {titles.map((title)=>
                <li key={title.id}>
                <div className="time-entry">
                    <strong>{title.title}</strong>
                    <div className="time"><strong>Lv: {title.name}</strong></div>
                </div>
                <div>
                <code><strong>Note:  </strong>{title.content}</code>
                </div>
                <hr/>
            </li>
                )}

            </ol>
            <Class1/>
            <br/>
        </div>
    )
}
export default TitleList;
