import React,{ useState } from 'react'
import app from './base.js'


const AddBlogEntryForm = () => {
    const [title, setTitle] =useState('')
    const [time, setName] =useState('')
    const [note,setContent] = useState('')

    function onSubmit(e) {
        e.preventDefault()

        app
        .firestore()
        .collection('blogs')
        .add({
            title,
            name,
            content
        })
        .then(() =>{
            setTitle('')
            setName('')
            setContent('')
        })
    }

    return(
        <form onSubmit={onSubmit} className="logform">
            <h3>Add Task</h3>
            <div>
                <label><strong>Blog Title: </strong></label>
                <input placeholder="Name task here..." className="expand loginput layoutbg" type="text" value={title} onChange={e=>setTitle(e.currentTarget.value)}/>
            </div>
            <br/>
            <div>
                <label><strong>Author's Name: </strong></label>
                <input placeholder="Indicate impotance here..." className="expand loginput layoutbg" type="number" value={name} onChange={e=>setName(e.currentTarget.value)}/>   
            </div>
            <br/>
            <div>
                <label><strong>Blog: </strong></label>
                <input placeholder="Add note here..." className="expand loginput layoutbg" type="text" value={content} onChange={e=>setContent(e.currentTarget.value)}/>   
            </div>
            <br/>
            <button variant="primary" className="expand"><strong>Add</strong></button>

        </form>
    )
}

export default AddBlogEntryForm;
