import React from "react";
import firebase from "../firebase/base";
import { SpellInput_5800 } from "./cis5800_add_anno";
import {Button} from 'react-bootstrap';

class CisAnno_5800 extends React.Component {
  //I need courseID
  state = {
    spell:[],
    newSpellName: null
  }
  componentDidMount(){
      const db = firebase.firestore()
      db.doc(this.props.state.userCourse).subcollection('announcement').get().then(snapshot => {
        const spellsData = [];
        snapshot.forEach(doc =>
          spellsData.push({ ...doc.data(), id: doc.id })
        )
          console.log(spellsData)
          this.setState({spell: spellsData})
      })

  const onCreate = () => {
    const db = firebase.firestore();
    db.doc(this.props.state.userCourse).collection('announcement').add({ name: this.state.newSpellName });
  };
}
render(){
  console.log(this.props.state.userCourse)
  return (
    <div>
      {this.state.spell.map(spell => (
        <div key={spell.name}>
          <SpellInput_5800 spell={spell}/>
        </div>
      ))}

      <textarea
        value={this.state.newSpellName}
        onChange={e => this.setState({newSpellName: e.target.value})}
        className="inputwidth expand"
        placeholder="Enter here..."
      />

      <Button variant="primary" className="addwidth expand" onClick={this.onCreate}><strong>+</strong></Button>
      <br/>
    </div>

  );
}
}

export default CisAnno_5800;
