import React, { useCallback } from "react";
import { withRouter } from "react-router";
import firebase from "./base";
import baruch from './baruchlogo.png';
import Container from 'react-bootstrap/Container';
import {Button,Row} from 'react-bootstrap';
import Col from 'react-bootstrap/Col';
import{Link} from 'react-router-dom';
import bearcats from './Bearcats.png';

const SignUp = ({ history }) => {
  const handleSignUp = useCallback(async event => {
    event.preventDefault();
    const { email, password } = event.target.elements;
    try {
      await firebase
        .auth()
        .createUserWithEmailAndPassword(email.value, password.value);
      history.push("/");
    } catch (error) {
      alert(error);
    }
  }, [history]);

  return (
    <div>
    <div className="start">
        <center><img src={baruch} width="200px" alt="baruch logo" className="startpageicon"/></center>
        <Container>
            <Row>
                <Col md={6}>
                <div className="Login">
                <strong><h2>Please Sign Up</h2></strong>
                <hr/>
                <form onSubmit={handleSignUp}>
                  <label className="expand">
                    <strong>Email</strong><br/>
                    <input name="email" type="email" placeholder="Email" />
                  </label>
                  <label className="expand">
                    <strong>Password</strong><br/>
                    <input name="password" type="password" placeholder="Password" />
                  </label>
                  <hr/>
                  <Button type="submit">Sign Up</Button>
                  <br/>
                  <Button componentClass={Link} href="/login" to="/">Go Back to Login</Button>
                </form>
              </div>

                </Col>
                <Col md={6}>
                <img src={bearcats} width="100%" alt="bearcats" className="startpageicon"/>
                <h3><strong>Welcome To Baruch</strong></h3>
                </Col>
            </Row>

        </Container>
        </div>
        </div>
  );
};

export default withRouter(SignUp);
