import React from "react";
import firebase from '../firebase/base'
import {Button,ButtonGroup} from 'react-bootstrap';

export const SpellInput = ({ spell }) => {
  const [name, setName] = React.useState(spell.name);

  const onUpdate = () => {
    const db = firebase.firestore()
    db.collection('spells').doc(spell.id).set({...spell, name})
  }

  const onDelete = () => {
    const db = firebase.firestore()
    db.collection('spells').doc(spell.id).delete()
  }

  return (
    <>
      <textarea className="expand todolist"
        value={name}
        onChange={e => {
          setName(e.target.value);
        }}
      />
      <ButtonGroup vertical className="buttonheight">
      <Button variant="primary" size="sm" className="buttonsize expand" onClick={onUpdate}><h3><strong>^</strong></h3></Button>
      <Button variant="primary" size="sm" className="buttonsize expand" onClick={onDelete}><h3><strong>-</strong></h3></Button>
      </ButtonGroup>
      <hr/>
    </>
  );
};
