import React from "react";
import firebase from "../firebase/base";
import { cis4160_add_anno } from "./cis4160_add_anno";
import {Button} from 'react-bootstrap';

function cis4160_anno() {
  const [annos, setAnnos] = React.useState([]);
  const [newAnnoName, setNewAnnoName] = React.useState();

  React.useEffect(() => {
      const db = firebase.firestore();
      const unsubscribe = db.collection("CIS4160_Announcement").onSnapshot(snapshot => {
        const annosData = [];
        snapshot.forEach(doc => annosData.push({ ...doc.data(), id: doc.id }));
        setAnnos(AnnosData);
      });
    return unsubscribe
  }, []);

  const onCreate = () => {
    const db = firebase.firestore();
    db.collection("CIS4160_Announcement").add({ name: newAnnoName });
  };

  return (
    <div>
    <hr/>

    <ol>
      {annos.map(anno => (
        <li key={anno.name}>
          <annoInput anno={anno}/>
        </li>
      ))}
    </ol>
      <textarea
        value={newannoName}
        onChange={e => setNewAnnoName(e.target.value)}
        className="inputwidth expand"
        placeholder="Enter here..."
      />
      <Button variant="primary" className="addwidth expand" onClick={onCreate}><strong>+</strong></Button>
      <br/>
    </div>
  );
}

export default cis4160_anno;
