import React, {useState} from 'react';
import {Tabs, Tab, Button} from 'react-bootstrap'
import Calendar from './Calendar'
import {Link} from 'react-router-dom'
import firebase from "../firebase/base";
function HomeWorkList(state) {

    const [key, setKey] = useState('Courses');

    return (
      <Tabs id="controlled-tab-example" activeKey={key} onSelect={k => setKey(k)}>
        <Tab eventKey="Courses" title="Courses">
        {
          this.state.registeredCourse.map((course, index) =>{
            const reg = this.state.userCourse[index]
            return(
            <div className="courses">
              <Link className="icon expand" to={{pathname:'/StudentPage', state:{registeredCourse: course,
                                                                              userCourse: reg}}}><Button variant="primary"><strong>{course}</strong></Button></Link>
            </div>
              )
            })
          }
        </Tab>
        <Tab eventKey="Calendar" title="Calendar">
          <Calendar/>
        </Tab>
      </Tabs>
);
}
export default HomeWorkList;
