import React, { Component } from 'react';
import './App.css';
import 'bootstrap/dist/css/bootstrap.min.css';
import{BrowserRouter as Router,Route,Switch} from 'react-router-dom';
import{withRouter} from 'react-router-dom';
import Homepage from './components/Homepage';
import AboutPage from './components/AboutPage'
import {TransitionGroup, CSSTransition} from "react-transition-group";

import './pageTransitions/slideTransition.scss';

class App extends Component {
  constructor(props) {
  super(props);
  this.state = {
    //Get initial depth of current page 
    prevDepth: this.getPathDepth(this.props.window.location)
  };
}

  componentWillReceiveProps() {
  //When props are updated, update the current path 
  //props supplies us with the location object which has a router location info
  this.setState({ prevDepth: this.getPathDepth(this.props.window.location) });
}

  getPathDepth(location) {
    let pathArr = window.location.pathname.split("/");
    pathArr = pathArr.filter(n => n !== "");
    return pathArr.length;
  }

  render(){
    const { locaiton } = this.props;
    const currentKey = window.location.pathname.split("/")[1] || "/";
    const timeout = { enter: 800, exit:400};

  return (
    <Router>
    <div className="App"> 
      <header className="App-header">
        <TransitionGroup component = "div" className="pusher">
        <CSSTransition key={currentKey} timeout={timeout} className="pageSlider fade" mounOnEnter={false} unmountOnExit={true}>
        <div
          className={
            this.getPathDepth(window.location) - this.state.prevDepth >= 0
              ? "left"
              : "right"
          }
          >
        <Switch location ={window.location}>
        <Route path='/' exact component={Homepage} />
        <Route path='/AboutPage' exact component={AboutPage} />
        </Switch>
        </div>
        </CSSTransition>
        </TransitionGroup>
      </header>


      <footer>Created By ZHENGJIAN LIU</footer>
    </div>
    </Router>

  );
}
}
export default withRouter(App);
