import React from "react";
import firebase from '../firebase/base'
import {Button} from 'react-bootstrap';

export const SpellInput = ({ spell }) => {
  const [name, setName] = React.useState(spell.name);

  const onUpdate = () => {
    const db = firebase.firestore()
    db.collection('spells').doc(spell.id).set({...spell, name})
  }

  const onDelete = () => {
    const db = firebase.firestore()
    db.collection('spells').doc(spell.id).delete()
  }

  return (
    <>
    <div className="blog"> 
      <textarea className="expand todolist"
        value={name}
        onChange={e => {
          setName(e.target.value);
        }}
      />
      <div>
      <Button variant="primary" className=" expand" onClick={onUpdate}><strong>Update</strong></Button>
      <Button variant="primary" className=" expand" onClick={onDelete}><strong>Delete</strong></Button>
      </div>
      </div>
      <hr/>
    </>
  );
};
