import React,{useState, useEffect} from 'react';
import app from './base.js'
import Class1 from './popwindow';

const SORT_OPTIONS = {
    'TIME_ASC': {column: 'time_seconds', direction:'asc'},
    'TIME_DESC': {column: 'time_seconds', direction:'desc'},
    'TITLE_ASC': {column: 'title', direction:'asc'},
    'TITLE_DESC': {column: 'title', direction:'desc'},
}

  const onUpdate = () => {
    const db = firebase.firestore()
    db.collection('spells').doc(spell.id).set({...spell, name})
  }

  const onDelete = () => {
    const db = firebase.firestore()
    db.collection('spells').doc(spell.id).delete()
  }

function useTimes(sortBy= 'TIME_ASC') {
    const [times,setTimes] =useState([])

    useEffect(()=>{
        const unsubscribe = app
        .firestore()
        .collection('tasks')
        .orderBy(SORT_OPTIONS[sortBy].column, SORT_OPTIONS[sortBy].direction)
        .onSnapshot((snapshot) =>{
            const newTimes = snapshot.docs.map((doc) => ({
                id: doc.id,
                ...doc.data()
            }))
            setTimes(newTimes)
        })
        return() =>unsubscribe()
    }, [sortBy])

    return times
}

const TimesList = () =>{
    const [sortBy ,setSortBy] = useState('TIME_DESC')
    const times = useTimes(sortBy)

    return(
        <div>
            <br/>
            <h2>To Do List:</h2> 
            <hr/>
            <div >
                <label><strong>Sort: </strong></label>{' '}
                 <select value={sortBy} onChange={e => setSortBy(e.currentTarget.value)} >
                     <option value="TIME_DESC">Task Most Important</option>
                     <option value="TIME_ASC">Task Less Imporant</option>
                     <option disabled>---</option>
                     <option value="TITLE_ASC">A-Z</option>
                     <option value="TITLE_DESC">Z-A</option>
                 </select>
            </div>
            <ol>
                {times.map((time)=>
                <li key={time.id}>
                <div className="time-entry">
                    <strong>{time.title}</strong>
                    <div className="time"><strong>Lv: {time.time_seconds}</strong></div>
                </div>
                <div>
                <code><strong>Note:  </strong>{time.note}</code>
                    <button onClick={onUpdate}>Update</button>
                    <button onClick={onDelete}>Delete</button>
                </div>
                <hr/>
            </li>
                )}

            </ol>
            <Class1/>
            <br/>
        </div>
    )
}
export default TimesList;
